import React, {useContext, useState} from "react";
import {UserContext} from "../contexts/UserContext";

const Login = () => {

    const {user, setUser} = useContext(UserContext);

    const [usernameEmail, setUsernameEmail] = useState('')
    const [password, setPassword] = useState('')

    const handleLogin = (e) => {
        e.preventDefault();
        fetch('http://localhost:3000/login_authentication', {
            method: 'post',
            credentials: 'include',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                username_email: usernameEmail,
                password: password,
            })
        }).then(response => response.json().then(data => {
            setUser(data['user']);
        })).catch((error) => {
            console.error(error);
        });
    }

    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleLogin}>
                <div className="relative">
                    <label htmlFor="required-email" className="text-gray-700">
                        Email
                        <span className="text-red-500 required-dot">*</span>
                    </label>
                    <input type="text" id="required-email" value={usernameEmail} onChange={e => setUsernameEmail(e.target.value)}
                           className=" rounded-lg border-transparent flex-1 appearance-none border border-gray-300 w-full py-2 px-4 bg-white text-gray-700 placeholder-gray-400 shadow-sm text-base focus:outline-none focus:ring-2 focus:ring-purple-600 focus:border-transparent"
                           name="email_username" placeholder="Your email or username"/>
                </div>
                <div className="relative">
                    <label htmlFor="required-email" className="text-gray-700">
                        Email
                        <span className="text-red-500 required-dot">*</span>
                    </label>
                    <input type="password" id="required-password" value={password} onChange={e => setPassword(e.target.value)}
                           className=" rounded-lg border-transparent flex-1 appearance-none border border-gray-300 w-full py-2 px-4 bg-white text-gray-700 placeholder-gray-400 shadow-sm text-base focus:outline-none focus:ring-2 focus:ring-purple-600 focus:border-transparent"
                           name="password" placeholder="Your password"/>
                </div>
                <button type="submit" className="py-2 px-4  bg-indigo-600 hover:bg-indigo-700 focus:ring-indigo-500 focus:ring-offset-indigo-200 text-white w-full transition ease-in duration-200 text-center text-base font-semibold shadow-md focus:outline-none focus:ring-2 focus:ring-offset-2  rounded-lg ">
                    Login
                </button>
            </form>
        </div>
    )
};

export default Login;
