import React, {useContext, useState} from "react";
import {UserContext} from "../contexts/UserContext";

const Login = () => {

    const { user } = useContext(UserContext);

    const [usernameEmail, setUsernameEmail] = useState('')
    const [password, setPassword] = useState('')

    const handleLogin = (e) => {
        e.preventDefault();
        fetch('http://localhost:3000/login_authentication', {
            method: 'post',
            credentials: 'include',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                username_email: usernameEmail,
                password: password,
            })
        }).then((response) => response.json().then((data => {

            })
        )).catch((error) => {
            console.error(error);
        });

        fetch('http://localhost:3000/badges/populate_badge_list?user_id=1', {
            method: 'GET',
            credentials: 'include',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json',
            }}).then((response) => response.json().then((async data => {
                console.log(data)
            })
        )).catch((error) => {
            console.error(error);
        });
    }

    return (
        <div>
            <h1>Login</h1>
            <form onSubmit={handleLogin}>
                <input type="text" name="email_username" value={usernameEmail} onChange={e => setUsernameEmail(e.target.value)} />
                <input type="password" name="password" value={password} onChange={e => setPassword(e.target.value)} />
                <button type="submit">Submit</button>
            </form>
        </div>
    )
};

export default Login;
